const accordion=document.querySelectorAll(".accordion");let mainParent,height,answer,btn;accordion.forEach((e=>{e.addEventListener("click",(()=>{btn=e.querySelector(".question-line__horizontal"),height=e.querySelector(".answer__wrapper").offsetHeight,answer=e.querySelector(".answer"),mainParent=e,mainParent.classList.contains("active")?(mainParent.classList.remove("active"),btn.classList.remove("line__active"),answer.style.height="0px"):(mainParent.classList.add("active"),btn.classList.add("line__active"),answer.style.height=`${height}px`)}))}));